procedure COMPILE_INVALID_OBJECTS is
BEGIN

     FOR CUR IN (
                SELECT O.OBJECT_NAME,O.OBJECT_TYPE
                FROM USER_OBJECTS O
                WHERE O.STATUS = 'INVALID'
     ) LOOP
     BEGIN
         IF cur.object_type = 'PACKAGE BODY' THEN
             EXECUTE IMMEDIATE 'ALTER PACKAGE '||CUR.OBJECT_NAME||' COMPILE ';
         ELSE
             EXECUTE IMMEDIATE 'ALTER '||CUR.OBJECT_TYPE||' '||CUR.OBJECT_NAME||' COMPILE ';
         END IF;

         DBMS_OUTPUT.PUT_LINE(CUR.OBJECT_TYPE||' '||CUR.OBJECT_NAME||' COMPILED ');
     EXCEPTION 
         WHEN OTHERS THEN
            DBMS_OUTPUT.PUT_LINE(CUR.OBJECT_TYPE||' '||CUR.OBJECT_NAME ||' COMPILED WITH ERRORS ');
     END;  
     END LOOP;

end COMPILE_INVALID_OBJECTS;
