	TYPE REC2 IS RECORD
	(
		TBL_NAME VARCHAR2(80),
    COL_NAME VARCHAR2(80)
	);
	TYPE TABLE_LIST IS TABLE OF REC2
  INDEX BY BINARY_INTEGER	;


  FUNCTION IS_REF_COLUMNS2 (TBL_NAME VARCHAR2,COL_VALUE VARCHAR2) RETURN VARCHAR2;
  FUNCTION IS_EXIST2(TBL_NAME VARCHAR2,COL_NAME VARCHAR2,VAL VARCHAR2)RETURN BOOLEAN;
  FUNCTION IS_REF_COLUMNS2 (TBL_NAME VARCHAR2,COL_VALUE VARCHAR2,LST out TABLE_LIST) RETURN VARCHAR2;
end DEV_TABLE_OPER2;
