package body DEV_TABLE_OPER2 is
   FUNCTION IS_REF_COLUMNS2 (TBL_NAME VARCHAR2,COL_VALUE VARCHAR2 ,LST out TABLE_LIST)
    RETURN VARCHAR2
    IS
--   TEMP TEMP_VARRAY2;
     rec rec2;
     List table_list;
     test number :=null;
    CURSOR C_ IS
    select c_c.constraint_name ,c_c.table_name T_NAME,c_c.column_name C_NAME
						from 
						user_cons_columns C_C,
						(
								select u1.constraint_name cons_name,
									u1.constraint_type type_,
									u1.table_name pk_table_name,
									u2.constraint_name r_cons_name,
									u1.table_name table_name 
								from
									user_constraints u1,
									user_constraints u2
									where u1.constraint_name = u2.r_constraint_name
									and u1.constraint_type like 'P'
							)u

						where 
							c_c.constraint_name = u.r_cons_name
						and
							u.pk_table_name lIke upper(TBL_NAME);

    R_TEMP REC2; 
    I_COUNTER NUMBER :=1;
    BEGIN
    list :=lst;
			FOR POINTER IN C_ LOOP
				IF IS_EXIST2(POINTER.T_NAME,POINTER.C_NAME,COL_VALUE)= TRUE THEN
            rec.tbl_name := POINTER.T_NAME;
            rec.col_name := POINTER.c_name;
            LiST(LiST.count+1):=rec;
            test:=list.Count;
				END IF;
			END LOOP;	
      test:=list.Count;
      lst := list;
      if list.Count = 0 then
         return null;
      end if;
      rec := lst(1);
			RETURN rec.tbl_name; 
    END;    
