FUNCTION Fun_Avail_Bs_Dzn_SGD
(PARM_SO_ID NUMBER,
 PARM_BS_ITEM_ID NUMBER,
 PARM_PAIR_RATIO_PACK NUMBER,
 PARM_SO_DET_ID NUMBER

)

RETURN NUMBER IS


lnm_TXT_brd_DZN            number(16,2);
Lnm_Txt_Aval_Bs_Dzn        Number(16, 2);
  Lnm_Tot_Prod_Dzn         Number(16, 2);
  Lnum_Adj_Qty             Number(16, 2);
  Lnum_Wst_Qty             Number(16, 2);
  Lnum_Left_Qty            Number(16, 2);
  Lnum_From_Transfer_Dzn   Number(16, 2);
  Lnum_Too_Transfer_Dzn    Number(16, 2);
  lnum_leftover_to_order_trans number(16,2);

  lnum_bs_item_id  number;

Begin
  lnum_bs_item_id := PARM_BS_ITEM_ID;

---------------------------Total boarding Dzn
select sum(nvl(mt.PRESSED_QTY_DZN, 0) + ((nvl(mt.PRESSED_QTY_PCS, 0) / 24)))
INTO lnm_TXT_brd_DZN
  from sgd_boarding_entry_mt    mt,
       sgd_shift_mt             s,
       wiz_segment02            s2,
       code_combination_values  ccv,
       SGD_MACHINE_OPERATOR_DET sic,
       sgd_batch_making_det     bd,
       sgd_batch_making_mt      bm,
       sgd_stm_planning_det     pd,
       employee_mt              e,
       imk_so_mt                so,
       SGD_MACHINE_OPERATOR_DET incrg,
       sgd_setup_plant_mt       pl,
       sgd_sample_request_det   srd,
       items_category           ic,
       code_combination_values  ccvbs

 where mt.shift_id = s.shift_id(+)
   and mt.operation_strct_id = s2.structure_id
   and mt.operation_seg2_id = s2.segment_id
   and mt.cc_comb_id = ccv.code_combination_id
   and mt.CC_STRUCT_ID = cc
