function fun_batch_exist_OSR(parm_batch_id number)return number is
     lnm_exist number;
     begin
             select count(1) Into lnm_exist
             from  temp_sgd_dup_record
             where column_1 = nvl(parm_batch_id,-1)
             and nvl(column_2,0) = 1;

       if nvl(lnm_exist,0) = 0 then

         select count(1) Into lnm_exist
          from  SGD_OS_PROCESS_RETURN_det
           where OS_PROCESS_DET_ID = nvl(parm_batch_id,-1)
          and nvl(FINAL_RECEIPT_YN,0) = 1
          and OS_PROCESS_RETURN_ID <> nvl(parm_batch_id,-1);

       end if;


       return lnm_exist;
     end;
