function FUN_GET_PACKNG_CC_SGD(PARM_STM_PLAN_DET_ID NUMBER) return varchar2 is
  Result varchar2(10000);
   flag  number := 0;
begin
  FOR cur IN (
                 select
                   pack_auth.stm_plan_det_id
                  ,REPLACE(VCC.CODE_VALUE,'-','') PACKING_UNIT

                  from
                   sgd_pack_auth_pair_code_mt pack_auth
                  ,view_code_combinations  vcc

                  where pack_auth.cost_code_id   = VCC.CODE_COMBINATION_ID
                  AND pack_auth.stm_plan_det_id = PARM_STM_PLAN_DET_ID
               ) LOOP

        IF flag = 1 THEN
             RESULT := RESULT||',';
             END IF;

    RESULT := RESULT||cur.packing_unit;
    flag := 1;

    END LOOP;

  return(Result);
end FUN_GET_PACKNG_CC_SGD;
