function FUN_GET_SRD_SPECIAL_INSTRUCT(PARM_SAMPLE_REQ_DET_ID NUMBER) return varchar2 is

  Result Varchar2(5000);
    flag  number := 0;
begin

FOR CUR IN(
                select distinct sp.special_instruction from sgd_sreq_special_instruct_det d , SGD_SR_SPECIAL_INSTRUCTIONS SP
                WHERE D.INSTRUCT_ID = SP.INSTRUCT_ID
                AND D.SAMPLE_REQUEST_DET_ID = PARM_SAMPLE_REQ_DET_ID
                  ) LOOP
           IF flag = 1 THEN
             RESULT := RESULT||',';
             END IF;

                  RESULT := RESULT||CUR.SPECIAL_INSTRUCTION;
                 flag := 1;
                 END LOOP;
  return(Result);
end FUN_GET_SRD_SPECIAL_INSTRUCT;
