function fun_round_on_base
(
       p_number number,
       p_base number
)
       return number  is
       p_round_val number;
begin
       select decode((round((p_number/p_base),0)),trunc(p_number/p_base),trunc(p_number/p_base)*p_base,(round((p_number/p_base),0))*p_base) into p_round_val from dual; 
       return(p_round_val);
end fun_round_on_base;
