function fun_sgd_get_CTN_wise_wt_kg(parm_so_id number, parm_stm_plan_id number,parm_ctn_id number) return number is
lnm_weight_kg number(18,4);
lnm_QTY_IN_DOZEN number(18,2);
begin

    select
        sum(round(fun_sgd_get_CTN_ps_weight_kg(m.sale_order_id,
                                               m.stm_plan_id,
                                               d.so_det_id) * nvl(d.QTY_DZN,0),
                  4))
    INTO      lnm_weight_kg
   from sgd_carton_det d, sgd_carton_mt m, sgd_carton_sr_mt s
  where d.sgd_carton_mt_id = m.sgd_carton_mt_id
    and m.sr_id = s.carton_sr_id
    and m.sale_order_id = nvl(parm_so_id,-1)
    and m.stm_plan_id = nvl(parm_stm_plan_id,-1)
    and m.sr_id = nvl(parm_ctn_id,-1);


   return (lnm_weight_kg);

exception when others then
  return -1;
end fun_sgd_get_CTN_wise_wt_kg;
