function fun_sgd_os_rtn_unproced_yarn(parm_os_process_Det_id number)
  return number is
  lnm_unproceed_yrn_rtn number;
begin

  select sum(d.material_return_qty)
    Into lnm_unproceed_yrn_rtn
    from SGD_OS_PROCESS_RETURN_det d, sgd_os_process_det pd
   where d.os_process_det_id = pd.os_process_det_id
     and d.os_process_det_id = nvl(parm_os_process_det_id, -1);

  return(nvl(lnm_unproceed_yrn_rtn, 0));

end fun_sgd_os_rtn_unproced_yarn;
