FUNCTION get_accounting_segment(STRCT NUMBER) RETURN NUMBER IS

--- THIS FUNCTION WILL RETURN THE ACCOUNTING SEGMENT NUMBER OF GIVEN STRUCTURE_ID

--- PARAMETERS:-
---
--- STRCT  -> THE STRUCTURE ID OF REQUIRED STRUCTURE'S ACCOUNTING SEGMENT
---

RET NUMBER;
BEGIN

	SELECT SEGMENT_NO INTO RET
	FROM WIZ_SEGMENT_DEFINITION
	WHERE QUALIFIER_ID=3 AND
				STRUCTURE_ID=STRCT;


	RETURN(RET);

EXCEPTION  
WHEN NO_DATA_FOUND THEN
  RETURN(NULL);
WHEN TOO_MANY_ROWS THEN
  RETURN(NULL);

END;
