function GET_CLOCK_ID(PARM_DATE Date , PARM_SHIFT_ID  Number ,parm_en_next_poss_val Number Default 0) return number is
  Result NUMber;
begin
  Begin
      Select CB.PAY_SHIFT_CLOCK_ID Into RESULT
      From PAY_SHIFT_CLOCKS_MT CM , PAY_SHIFT_CLOCK_BINDING_MT CB
      Where CM.CLOCK_ID= CB.PAY_SHIFT_CLOCK_ID
      And     CB.PAY_SHIFT_ID = PARM_SHIFT_ID
      And     PARM_DATE Between CB.DATE_FROM and cb.date_to;
      Return RESULT;
   End;
  Exception When Others Then
  Return Null;
end GET_CLOCK_ID;
