FUNCTION GET_DEPENDENCY(STRCT NUMBER,SEG NUMBER) RETURN VARCHAR2 IS

-----------------------------------------------------------------------------------------
------------ THIS FUNCTION RETURNS A COMMA SEPARATED DEPENDENCY STRING FOR A SPECIFIC 
------------ SEGMENT NUMBER
--------- BELLOW IS THE DETAIL OF PARAMETERS
----- STRCT  ->  THE STRUCTURE NUMBER
----- SEG    ->  THE SEGMENT NUMBER
-------------------------------------------------------------------------------------------


DEP NUMBER;
TMP VARCHAR2(2000);

BEGIN

  SELECT DEPENDENT_ON_SEGMENT_NO INTO DEP
  FROM WIZ_SEGMENT_DEFINITION
  WHERE STRUCTURE_ID = STRCT AND
  			SEGMENT_NO   = SEG;

  IF DEP IS NULL THEN
  	RETURN(NULL);
  ELSE
  	TMP := GET_DEPENDENCY(STRCT ,DEP);
  	IF TMP IS NOT NULL THEN	
  			RETURN(TMP||','||DEP); 
  	ELSE
  		  RETURN(DEP);
  	END IF;
  END IF;

END;
