FUNCTION GET_DEPENDENT_SEG(SEG_NO IN VARCHAR2, STRUC_ID IN NUMBER) return varchar2 is
  Result            VARCHAR2(200);
  lvch_q_str        VARCHAR2(200);
BEGIN

     lvch_q_str := SUBSTR(SEG_NO,13,1);
     Result     := SUBSTR(SEG_NO,13,1);
     LOOP
        BEGIN
          SELECT  wsd.dependent_on_segment_no
          INTO    lvch_q_str
          FROM    wiz_segment_definition wsd
          WHERE   wsd.structure_id= STRUC_ID AND
                   wsd.segment_no= lvch_q_str;
          IF lvch_q_str IS NOT NULL THEN
            Result :=Result ||','||lvch_q_str;
          END IF;
        EXCEPTION 
          WHEN NO_DATA_FOUND THEN
          EXIT ;
        END ;
     END LOOP;
  RETURN (Result);
END GET_DEPENDENT_SEG;
