function get_hierarchy_orderby_string(parm_tab_name VARCHAR2,parm_pk1_col_name VARCHAR2,parm_fk1_col_name VARCHAR2,parm_orderby_col_name VARCHAR2,parm_pk1_col_value number,parm_pk2_col_name VARCHAR2 DEFAULT NULL,parm_fk2_col_name VARCHAR2 DEFAULT NULL,parm_pk2_col_value NUMBER DEFAULT NULL) return varchar2 is

  Result      VARCHAR2(2000);
  lnum_fk1_id NUMBER  (20);   
  lnum_fk2_id NUMBER  (20);  

begin


    IF parm_pk2_col_name IS NULL THEN 
        EXECUTE IMMEDIATE 'select '||parm_orderby_col_name||','||parm_fk1_col_name||' from '||parm_tab_name||' where '||parm_pk1_col_name||' = '||parm_pk1_col_value INTO result,lnum_fk1_id;
    ELSE
        EXECUTE IMMEDIATE 'select '||parm_orderby_col_name||','||parm_fk1_col_name||','||parm_fk2_col_name||' from '||parm_tab_name||' where '||parm_pk1_col_name||' = '||parm_pk1_col_value||' and '||parm_pk2_col_name||' = '||parm_pk2_col_value INTO result,lnum_fk1_id,lnum_fk2_id;    
    END IF;


    IF lnum_fk1_id IS NOT NULL THEN
       RESULT := get_hierarchy_orderby_string(parm_tab_name ,parm_pk1_col_name,parm_fk1_col_name,parm_orderby_col_name,lnum_fk1_id,parm_pk2_col_name,parm_fk2_col_name,parm_pk2_col_value)||RESULT;
    END IF;

    return(Result);

end get_hierarchy_orderby_string;
