function get_single_primary_key(parm_table_name varchar2,parm_column_name varchar2 default null,parm_primary_key number default null  , parm_parent_keys varchar2 default null) return number is
  LDAT_U_T_DATE date;
begin
  SELECT U_T_D
         INTO
         LDAT_U_T_DATE
  FROM WIZ_LICENCED_TO; 

  IF LDAT_U_T_DATE IS NULL OR Ldat_U_T_DATE < SYSDATE THEN
     RAISE_APPLICATION_ERROR(-20000,'Invalid PK Generation Method !');
  END IF;

  -- checking if parm_table_name starts with   PPK~   then the Pk should be computed Physically
  IF UPPER(substr(parm_table_name,1,4))='PPK~' THEN 
      return(get_physical_pk(SUBSTR(parm_table_name,5),parm_column_name ,parm_primary_key ,parm_parent_keys ));
  ELSE
      return(get_sequence_pk(parm_table_name,parm_column_name ,parm_primary_key ,parm_parent_keys ));
  END IF;
end get_single_primary_key;
