FUNCTION GET_SOURCE_CODE(PARM_NAME VARCHAR2,PARM_TYPE VARCHAR2) RETURN LONG IS
   LVCH_SOURCE LONG;
   LNUM_CNT NUMBER:= 0;
BEGIN

     FOR CUR IN  (
         SELECT * 
         FROM USER_SOURCE S
         WHERE S.NAME = UPPER(PARM_NAME) AND
               S.TYPE = UPPER(PARM_TYPE) 
     )    
     LOOP
         LNUM_CNT := LNUM_CNT +1;
         IF LNUM_CNT = 1 AND 
            INSTR(UPPER(CUR.TEXT),'WRAPPED') <> 0 THEN
            LVCH_SOURCE := 'N/A';
            EXIT;
         END IF;
         LVCH_SOURCE := LVCH_SOURCE||CUR.TEXT;
     END LOOP;

RETURN(LVCH_SOURCE);

END;
