function get_str(CODE VARCHAR2 , search_crit VARCHAR2, sep varchar2 ,las NUMBER default 0) return VARCHAR2 is
  Result VARCHAR2(50);
  lvch_str VARCHAR2(50);
begin
  FOR cnt IN  1.. how_many_chars(search_crit,'-')+1 LOOP
     Result :=   get_value_at(CODE,cnt,'-');
     IF lvch_str IS NOT NULL THEN
       lvch_str := lvch_str||sep||Result;
     ELSE
       lvch_str := lvch_str||Result;
     END IF;
  END LOOP;
  Result :=lvch_str;
  return(Result);
end get_str;
