FUNCTION GET_UNIT_OF_MEASURE(STRCT NUMBER ,ACC_SEG_ID NUMBER) RETURN VARCHAR2 IS

UOMID NUMBER;
RET VARCHAR2(2000);

BEGIN

			SELECT A.UOM_ID INTO UOMID
			FROM ACCT_UOM_ATTACHMENT A,ACCT_SEGMENT_MIRROR B
			WHERE A.ACCT_SEG_MIRROR_ID = B.ACCT_SEG_MIRROR_ID AND
            B.STRUCTURE_ID=STRCT
			AND B.SEGMENT_ID=ACC_SEG_ID;

--SUBSTR(CODE_COMBINATIONS.COMBINATION_ID_ALL,GET_ACCOUNTING_SEGMENT((CODE_COMBINATIONS.STRUCTURE_ID-1)*5+1)))))

		SELECT UOM_SHORT_DESC INTO RET
		FROM WIZ_UOM_MT
		WHERE UOM_ID=UOMID;

RETURN(RET);

EXCEPTION  
WHEN NO_DATA_FOUND THEN 
     RETURN(NULL);

END;
