FUNCTION get_value_at(CODE VARCHAR2 , LOC NUMBER , sep varchar2 ,las NUMBER default 0) RETURN varchar2 IS

 --- THIS FUNCTION WILL RETRIEVE AND RETURN THE CODE VALUE OF REQUIRED SEGMENT NUMBER
 --- PARAMETER DETAL:-
 ---		CODE  --> FULLY CONCATENATED STRING SEPARATED BY ANY SPECIFIC CHARACTER
 ---  	LOC   --> THE SEGMENT NUMBER FOR WHICH THE VALUE IS REQUIRED
 ---		SEP   --> THE SEPARATER CHARACTER USED TO IDENTIFY DIFFERENT SEGMENTS
 ---    LAS   --> THIS BOOLEAN PARAMATER WILL SHOW THAT WHEATHER THE SEPARATOR IS USED TO END THE STRING

 -------------------------------------------------------

CNT NUMBER;
CH VARCHAR2(1000);
ST NUMBER(5);
EN NUMBER(5);

BEGIN

	IF LOC<1 THEN RETURN(NULL); END IF;

	CNT := HOW_MANY_CHARS(CODE,SEP,0);

	IF NOT LAS=1 THEN CNT:=CNT+1; END IF;

			ST := FUN_INSTR(CODE,SEP,LOC-1,0)+1;
 		EN := FUN_INSTR(CODE,SEP,LOC,0);

   IF ST>1 THEN
      ST:=ST+(LENGTH(SEP)-1);
   END IF;

 		IF EN=0 THEN EN:=LENGTH(CODE)+1; END IF;

   CH := SUBSTR(CODE,ST,EN-ST);


		RETURN(CH);

END;
