FUNCTION how_many_chars(STR VARCHAR2,CHR VARCHAR,SENSITIVE NUMBER DEFAULT 0) RETURN NUMBER IS

----------------------------------------------------------------------------------------------
-------------------------THIS FUNCTION RETURN THE NUMBER OF EXISTANCE OF SPECIFIC 
----------------------   CHARACTER IN A GIVEN STRING
----------------------------------------------------------------------------------------



RET NUMBER:=0;
A NUMBER;
BEGIN
	IF STR IS NULL THEN RETURN(0); END IF;
	FOR CNT IN 1..LENGTH(STR)-(LENGTH(CHR)-1) LOOP

		IF SENSITIVE=1 THEN
			IF SUBSTR(STR,CNT,LENGTH(chr))=CHR THEN RET:=RET+1; END IF;
		ELSE
			IF UPPER(SUBSTR(STR,CNT,LENGTH(chr)))=UPPER(CHR) THEN RET:=RET+1; END IF;
		END IF;

	END LOOP;
	RETURN(RET);
END;
