function is_code_lie_in_range(parm_code_to_check VARCHAR2, 
                                              parm_from_code VARCHAR2, 
                                              parm_to_code VARCHAR2) return NUMBER is
  lvch_frm_seg VARCHAR2(100);
  lvch_to_seg  VARCHAR2(100);
  lvch_code    VARCHAR2(100);

begin


  FOR cnt IN 1..how_many_chars(parm_code_to_check,'-')+1 LOOP

      lvch_frm_seg := get_value_at(parm_from_code,cnt,'-');
      lvch_to_seg  := get_value_at(parm_to_code  ,cnt,'-');    
      lvch_code    := get_value_at(parm_code_to_check  ,cnt,'-');

      IF NOT lvch_code BETWEEN lvch_frm_seg AND lvch_to_seg THEN 
         RETURN(0);
      END IF;

  END LOOP;

  return(1);

END ;
