function is_referenced_by_OLD(key_to_check varchar2,table_column_array varchar2) return number is

  Result number;

  TAB_COL_NAME VARCHAR2(500);-- THIS VAR CONTAINS THE COLUMN AND TABLE NAME OF CURRENT UNDERLAYING TABLE

  TABLE_NAME VARCHAR2(100); --  THIS WILL CONTAIN ONLY CURRENT TABLE NAME
  COLUMN_NAME VARCHAR2(100);--  THIS WILL CONTAIN ONLY COLUMNS OF CURRENT TABLE 

  SELEC VARCHAR2(1000);  -- THIS WILL BE USED TO GENERATE WHOLE SELECT STATEMENT
  FROME VARCHAR2(1000);  -- THIS WILL BE USED TO GENERATE FROM   CLAUSE OF SELECT STATEMENT
  WHER  VARCHAR2(1000);  -- THIS WILL BE USED TO GENERATE WHERE  CLAUSE OF SELECT STATEMENT

  DBMS_CUR NUMBER;  -- DYNAMIC CURSOR
  DBMS_ROWS NUMBER; -- NO.OF ROWS FETCHED

begin

  DBMS_CUR := DBMS_SQL.OPEN_CURSOR;

  FOR CNT IN 1..HOW_MANY_CHARS(TABLE_COLUMN_ARRAY,'/',0)+1 LOOP

        TAB_COL_NAME := GET_VALUE_AT(TABLE_COLumn_ARRAY,CNT,'/',0);

        TABLE_NAME  := SUBSTR(TAB_COL_NAME,1,INSTR(TAB_COL_NAME,'-')-1);
        COLUMN_NAME := SUBSTR(TAB_COL_NAME,INSTR(TAB_COL_NAME,'-')+1);


        WHER  := NULL;
        FOR WHE IN 1..HOW_MANY_CHARS(COLUMN_NAME,',',0)+1 LOOP
            IF WHER IS NULL THEN
               WHER := GET_VALUE_AT(COLUMN_NAME,WHE,',',0)||'='||GET_VALUE_AT(KEY_TO_CHECK,WHE,',',0);
            ELSE
               WHER := WHER || ' AND '||GET_VALUE_AT(COLUMN_NAME,WHE,',',0)||'='||GET_VALUE_AT(KEY_TO_CHECK,WHE,',',0);            
            END IF;

        END
