procedure log_debug_msg(parm_process_name varchar2,parm_error_desc varchar2) is
  PRAGMA AUTONOMOUS_TRANSACTION;

begin

  if pckg_fsg.gnum_debug_log_en = 1 then
      insert into temp_report_errors(report_name,error_string,fsg_submit_id)
      values (parm_process_name , parm_error_desc,PCKG_FSG.gnum_fsg_submit_id);
      COMMIT;
  end if;

end ;
