TRIGGER "SGDGROUP".mp_m_trg
 before insert or update on mp_m
  for each row
begin
  if inserting then
    :new.eby := SYS_CONTEXT('USERENV','HOST');
      :new.eip := SYS_CONTEXT('USERENV','IP_ADDRESS');
      :new.edat := sysdate;
  end if;
  if updating then
      :new.mby := SYS_CONTEXT('USERENV','HOST');
      :new.mip := SYS_CONTEXT('USERENV','IP_ADDRESS');
      :new.mdat := sysdate;
end if;
end;
