PROCEDURE NAYYER_FREEZ_UNFREEZ is

begin

BEGIN
PCKG_REPLICATION.SET_LOG_OFF;
PCKG_REPLICATION.SET_TRIGGERS_OFF;
END;

    UPDATE PERIOD_FREEZING_MT T
    SET T.FREEZE_YN = 1;

IF TO_CHAR(SYSDATE,'DD-MON-RRRR HH') = '24-JUN-2004 02' THEN --and
--      TO_CHAR(SYSDATE,'MI') BETWEEN  1 AND 30 THEN
    UPDATE PERIOD_FREEZING_MT T
    SET T.FREEZE_YN = 0
    WHERE T.PERIOD_ID in (20,22,23) ;
ELSE

    if SYSDATE < to_date('03-jul-2004') then
      UPDATE PERIOD_FREEZING_MT T
      SET T.FREEZE_YN = 0
      WHERE T.PERIOD_ID = 24;
    end if;

    if SYSDATE >= to_date('01-jul-2004')  then
      UPDATE PERIOD_FREEZING_MT T
      SET T.FREEZE_YN = 0
      WHERE T.PERIOD_ID = 25;
    end if;


END IF;

COMMIT;

BEGIN
PCKG_REPLICATION.SET_LOG_ON;
PCKG_REPLICATION.SET_TRIGGERS_ON;
END;



end NAYYER_FREEZ_UNFREEZ;
