    lnum_tot_rec_cnt NUMBER(14);
    lnum_ppl_rec_cnt NUMBER(14);
    lnum_lpr_rec_cnt NUMBER(14);
    lnum_man_rec_cnt NUMBER(14);
    lvch_rate_type   VARCHAR2(5);
    lnum_rate        NUMBER(14);
    ldat_po_date     DATE;
    lnum_team_id     NUMBER(14);
  BEGIN


    lnum_team_id := 1;

    RETURN(lnum_team_id);

  END;


  FUNCTION GET_PO_TEAM_ID(P_PO_ID NUMBER) RETURN NUMBER IS
    lnum_tot_rec_cnt NUMBER(14);
    lnum_ppl_rec_cnt NUMBER(14);
    lnum_lpr_rec_cnt NUMBER(14);
    lnum_man_rec_cnt NUMBER(14);
    lvch_rate_type   VARCHAR2(5);
    lnum_rate        NUMBER(14);
    ldat_po_date     DATE;
    lnum_team_id     NUMBER(14);
  BEGIN

    SELECT COUNT(1)
           INTO
           lnum_tot_rec_cnt
    FROM purchase_order_det pod
    WHERE pod.order_id = P_PO_ID;

    SELECT COUNT(1)
           INTO
           lnum_ppl_rec_cnt
    FROM purchase_order_det pod
    WHERE pod.order_id = P_PO_ID AND
          pod.rate_type = 'PPL';

    SELECT COUNT(1)
           INTO
           lnum_lpr_rec_cnt
    FROM purchase_order_det pod
    WHERE pod.order_id = P_PO_ID AND
          pod.rate_type = 'LPR';

    SELECT COUNT(1)
           INTO
           lnum_man_rec_cnt
    FROM purchase_order_det pod
    WHERE pod.order_id = P_PO_ID AND
          pod.rate_type = 'MAN';

