---FUNCTION TAKES STRING VALUE
---RETURN TRUE IF IT IS NUMERIC
---OTHERWISZE RETURN FALSE
FUNCTION FUN_ISNUMERIC(str VARCHAR2) RETURN BOOLEAN IS
	ASC_VAL NUMBER(14);
  FLAG BOOLEAN;
  find_dot NUMBER:=0;
BEGIN


  FOR CNT IN 1..LENGTH(str) LOOP
       ASC_VAL:=  ASCII(SUBSTR(str,CNT,1));

       IF ASC_VAL=46 THEN
         IF find_dot=0 THEN
           find_dot:=find_dot+1;
           FLAG:= TRUE;
         ELSE
           FLAG:=FALSE;
           EXIT;
         END IF;
       END IF;
       IF ASC_VAL>= 48 AND ASC_VAL<=57 THEN
            FLAG:=TRUE;
       ELSIF ASC_VAL <>46 THEN
            FLAG:=FALSE;
            EXIT;
       END IF;      
 	END LOOP;
 RETURN FLAG;
END;

---FUNCTION TAKES STRING VALUE
---RETURN TRUE IF IT IS CHAR
---OTHERWISZE RETURN FALSE


FUNCTION FUN_ISCHAR(str VARCHAR2) RETURN BOOLEAN IS
	ASC_VAL NUMBER(14);
  FLAG BOOLEAN;
BEGIN

 	FOR CNT IN 1..LENGTH(str) LOOP
       ASC_VAL:=  ASCII(SUBSTR(str,CNT,1));

       IF (ASC_VAL>= 65 AND ASC_VAL<=90) OR  (ASC_VAL>= 97 AND ASC_VAL<=122) THEN
            FLAG:=TRUE;
       ELSE
            FLAG:=FALSE;
            EXIT;
       END IF;      
 	END LOOP;
 RETURN FLAG;
END;

