Procedure PUSH_STACK (PARM_VALUE Varchar2) Is --------PUSH_STACK--
Begin
     LNUM_STK_POINTER:=LNUM_STK_POINTER+1;
     REF_TYP_STACK(LNUM_STK_POINTER):=PARM_VALUE;
End;


Function POP_STACK Return Varchar2 Is           --------POP_STACK--
LVC_VAL Varchar2(2000);
Begin
     LVC_VAL := REF_TYP_STACK(LNUM_STK_POINTER);
     REF_TYP_STACK.Delete(LNUM_STK_POINTER);
     LNUM_STK_POINTER:=LNUM_STK_POINTER-1;
     Return LVC_VAL;
     Exception When NO_DATA_FOUND Then
     raise_application_error(-20101,'Invalid Location');
End;

Function GET_STACK_SIZE Return Number Is           --------GET_STACK_SIZE--

Begin

     Return REF_TYP_STACK.Count ;
End;
