     FUNCTION IS_PARENT_NODE_YN(
                                parm_node_id    NUMBER,
                                parm_for_menu   VARCHAR2
                                ) RETURN VARCHAR2 IS
         lnum_is_parent  NUMBER;
         lvch_main_query VARCHAR2(1000);
     BEGIN
          lvch_main_query := '
                              SELECT
                                  COUNT(1)
                              FROM
                                  '||parm_for_menu||' MAIN_MENU
                              WHERE
                                  MAIN_MENU.NODE_PARENT = '||parm_node_id;

          EXECUTE IMMEDIATE lvch_main_query INTO lnum_is_parent;
          IF (lnum_is_parent > 0) THEN
             RETURN('Y');
          ELSE
             RETURN('N');
          END IF;
     END;
