       CURSOR COLSET IS SELECT COLDET_LINE_ID,
                               COLSET_DESC,
                               AMOUNT_TYPE,
                               PERIOD_TYPE,
                               CURRENCY_TYPE,
                               OFFSET_NO,
                               COL_SEQ_NO,
                               OVER_ROW_CALC_YN,
                               INVENTORY_DOC_TYPE,
                               INVENTORY_COL_TYPE,
