Procedure PRO_POP_DEPT_MATERIAL_COST(PARM_FROM_DATE Date,PARM_TO_DATE Date) is

 lnum_Dept_mat_cost_id Number;
begin
 Delete From Dept_Material_Cost t
 Where t.Month = to_char(to_date(PARM_FROM_DATE),'Mon-RR');
-- Commit;
    lnum_Dept_mat_cost_id := get_single_primary_key('DEPT_MATERIAL_COST','DEPT_MAT_COST_ID');
 For CUR In (Select
              JMC.Month          Month
             ,JMC.DEPT_ID        DEPT_ID
             ,JMC.ITEM_ID        ITEM_ID
             ,Sum(JMC.QTY)       QTY
             ,Sum(JMC.AMOUNT)    AMOUNT

            From job_material_cost JMC
            Group By
             JMC.MONTH,
             JMC.DEPT_ID,
             JMC.ITEM_ID

             )
 Loop

lnum_Dept_mat_cost_id := lnum_Dept_mat_cost_id +1;
  Insert Into DEPT_MATERIAL_COST(DEPT_MAT_COST_ID,MONTH,DEPT_ID,ITEM_ID,QTY,AMOUNT)
  Values(lnum_dept_mat_cost_id,CUR.MONTH,CUR.DEPT_ID,CUR.ITEM_ID,CUR.QTY,CUR.AMOUNT);
--COMMIT;
 End Loop;

--  Commit;
end PRO_POP_DEPT_MATERIAL_COST;
