package body PCKG_MATHS is

    function TO_BARCODE128(parm_type varchar2,parm_string VARCHAR2) RETURN VARCHAR2 IS
      Result VARCHAR2(100);
      lnum_total_no NUMBER(10):=0;
      LNUM_DIV      NUMBER;
      lch_substr char(1);
    begin

      FOR CUR IN 1..LENGTH(parm_string) LOOP
         lch_substr := substr(parm_string,cur,1);
         lnum_total_no := lnum_total_no + ( ascii(lch_substr)*cur );
      END loop;

      lnum_total_no := lnum_total_no + 103;

      LNUM_DIV := MOD(lnum_total_no ,103);

      RESULT := parm_string||ASCII(LNUM_DIV);
      return(Result);
    end ;

end PCKG_MATHS;
