package body pckg_sgd_costing is

  function fun_get_period_id(parm_As_on_date date,parm_inv_sob_id number) return number is 
    lnum_period_id number(14);


    begin
        SELECT period_id  INTO lnum_period_id
              FROM   period_details pd
              WHERE PERIOD_NO <> 999 AND
                    ( parm_As_on_date BETWEEN pd.from_date and pd.to_date) and
                    (pd.period_type_id,pd.calendar_id )= 
                                                   (SELECT book.perd_type_id,book.calendar_id
                                                    FROM wiz_gl_set_of_books book
                                                    where book.set_of_book_id = parm_inv_sob_id);

                      return      lnum_period_id;                         
    EXCEPTION 
              WHEN NO_DATA_FOUND THEN
                   RAISE_USER_DEFINED_ERROR(-20021); 
              WHEN TOO_MANY_ROWS THEN
                   RAISE_APPLICATION_ERROR(-20000,'TOO MANY ROWS IN GET_PERIOD_STATUS');
    end;
begin
  -- Initialization
null;
end pckg_sgd_costing;
