PROCEDURE pro_upd_iss_det_id( process_det_id   number,  prm_iss_det_id number) is

 begin
          update sgd_os_process_det d 
          set d.iss_det_id = prm_iss_det_id
          where d.os_process_det_id = process_det_id;
 end;

 PROCEDURE pro_set_null_iss_det_id( parm_process_id   number) is
 begin
          update sgd_os_process_det d 
          set d.iss_det_id = null
          where d.os_process_id = parm_process_id;
 end;

