PACKAGE BODY PCKG_UTILITY IS

   -- THIS VAR CONTAINS THE FRONT END LOGIN USER PREFERENCES
   -- IT WILL BE POPULATED THROUGH PRO_MENU_CNTRL/PRO_SCROL_CNTRL
   LGNUM_APPLICATION_USER         NUMBER(14);

   LGNUM_LOGIN_PERIOD_ID          NUMBER(14);
   LGNUM_LOGIN_BALANCING_SEG_ID   NUMBER(14);
   LGNUM_LOGIN_BALANCING_SEG_VAL  NUMBER(14);

   -------------------------------------------------------

   -- GLOBAL VAR FOR GET_CONT_GRP_ID.....
   LGNUM_LAST_GROUPBY_BASE NUMBER;
   LGNUM_LAST_GROUPBY_ID    NUMBER;
   --------------------

FUNCTION SUB_CONVERT_FUN(BLOK VARCHAR) RETURN VARCHAR ;--PROTOTYPE


----------------MAIN FUNCTION --------------------
FUNCTION FUN_CONVERT_WORD(LVAR_VALUE VARCHAR2) RETURN VARCHAR2 IS

	TYPE LVAR_PRINT  IS TABLE OF  VARCHAR2(80)
	INDEX BY BINARY_INTEGER;
	LVAR_ARRAY LVAR_PRINT;
  LVAR_RECEIVE LVAR_PRINT	;
  LNUM_ONLY NUMBER;
  LVAR_RESULT VARCHAR2(200);
  LVAR_RIGHT_AMOUNT VARCHAR2(200);
	LVAR_SKIP  VARCHAR2(15);

	LNUM_LENGTH_VARIABLE NUMBER;
	LNUM_LENGTH_VARIABLE1 NUMBER;
	LVAR_LEFT_AMOUNT VARCHAR2(200);
	LNUM_DECIMAL NUMBER;
  LNUM_AND NUMBER;


BEGIN

 	LNUM_LENGTH_VARIABLE:=LENGTH(LVAR_VALUE);
	LNUM_DECIMAL:=INSTR(LVAR_VALUE,'.');

	IF LNUM_DECIMAL=0 THEN
			LVAR_LEFT_AMOUNT:=SUBSTR(LVAR_VALUE,1,LNUM_LENGTH_VARIABLE);
	ELSE
			LVAR_LEFT_AMOUNT:=SUBSTR(LVAR_VALUE,1,LNUM_DECIMAL-1);
			LVAR_RIGHT_AMOUNT:=SUBSTR(LVAR_VALUE,LNUM_DECIMAL+1,2);
	END IF;


  LVAR_LEFT_AMOUNT:=LPAD(LVAR_LEFT_AMOUNT,12,'0');


	FOR  X IN 1..4 LOOP
			LVAR_ARRAY(X)
