package PCKG_WORKING is

  -- Author  : Tariq Sarwar
  -- Created : 30-09-2002 11:55:24 AM
  -- Purpose : it contains global var which will be accessable at runtime 
  --           to a form to show the status of current task

      FUNCTION get_current_number(parm_submit_id NUMBER) RETURN NUMBER;                
      FUNCTION get_total_number  (parm_submit_id NUMBER) RETURN NUMBER;
      FUNCTION get_percent       (parm_submit_id NUMBER) RETURN NUMBER;

      PROCEDURE set_current_number(parm_submit_id NUMBER,parm_c_num NUMBER);                
      PROCEDURE set_total_number  (parm_submit_id NUMBER,parm_t_num NUMBER);

      PROCEDURE set_working_status (parm_submit_id NUMBER,parm_working_stat NUMBER);

      PROCEDURE ONE_WORK_DONE(parm_submit_id NUMBER) ;

      FUNCTION CREATE_NEW_JOB RETURN NUMBER;

      FUNCTION  get_working_status (parm_submit_id NUMBER) RETURN NUMBER ;

      PROCEDURE set_custom_message (parm_submit_id NUMBER,parm_custom_message VARCHAR2);
      PROCEDURE set_form_title     (parm_submit_id NUMBER,parm_form_title     VARCHAR2);
      FUNCTION get_custom_message (parm_submit_id NUMBER) return VARCHAR2 ;
      FUNCTION get_form_title     (parm_submit_id NUMBER) return VARCHAR2 ;

      PROCEDURE enable_auto_message(parm_submit_id NUMBER) ;
      PROCEDURE disable_auto_message(parm_submit_id NUMBER) ;

end PCKG_WORKING;
