procedure POPULATE_temp_fsg_table(rowset_DET number,colset_DET number) is
       --it will be called for each of rowset(account assignment)
       --after this porc the populate fsg should be called to get the required 
       --output and insert it into the final table

    CURSOR ROWSET IS  SELECT FROM_ACCT,TO_ACCT,SET_OF_BOOK_ID,ACTIVITY_TYPE
                      FROM ROW_ACCT_ASSIGNMENT
                      WHERE ROWDET_LINE_ID=ROWSET_DET;

    CURSOR COLSET IS SELECT AMOUNT_TYPE,PERIOD_TYPE,CURRENCY_TYPE
                     FROM COLSET_DETAIL
                     WHERE COLSET_ID = Colset_Det;

    from_account     VARCHAR2(500);
    to_account       VARCHAR2(500);
    set_of_book      NUMBER(15);
    activity_type    VARCHAR2(10);

    amount_type      VARCHAR2(20);
    period_type      VARCHAR2(10);
    currency_type    VARCHAR2(20);
    generate_date    DATE:='10-JUL-2002';

    output           VARCHAR2(4000);
    DBMS_CUR         NUMBER;--  DBMS_SQL.CURSTYPE;
    DBMS_ROWS        NUMBER;

    TEMP_V VARCHAR2(100);
    TEMP_N NUMBER;

    COL_LOOP_COUNTER NUMBER;
---------------------------------------------------------------------------------    
    TYPE CSET IS RECORD(COLNUM NUMBER,A_TYPE VARCHAR2(200),P_TYPE VARCHAR2(200),C_TYPE VARCHAR2(200));

    TYPE CTAB IS TABLE OF CSET INDEX BY BINARY_INTEGER;

    COL_DEFINITION CTAB;
----------------------------------------------------------------------------------    
    CURSOR
