procedure proc_formula_level(vItem_id in number, vPrevLevel in out number, vFinalLevel in out number) is
  Result number;
  vBuffer number;
begin
  vPrevLevel:=vPrevLevel+1;
  if vPrevlevel>nvl(vFinalLevel,0) then
     vFinalLevel:=vPrevLevel;
  end if;
  for c1 in (select iqd.item_id from inv_qntv_formula_mt iqm, inv_qntv_formula_det iqd 
                   where iqm.inv_qntv_formula_id=iqd.inv_qntv_formula_id
                   and iqm.item_id=vItem_id)
  loop
     proc_formula_level(c1.item_id,vPrevLevel,vFinalLevel);     
     vPrevLevel:=0;
  end loop;     
  return;
end proc_formula_level;
