PROCEDURE PRO_GET_RANGE(PARM_SET_OF_BOOK_ID    IN NUMBER,
                                          PARM_GENERATION_DATE   IN DATE,
                                          PARM_PERIOD_TYPE       IN VARCHAR2,
                                          PARM_FROM_DATE         OUT DATE,
                                          PARM_TO_DATE           OUT DATE,
                                          PARM_ADJUSTMENT_PRD_ID OUT NUMBER) IS

  LNUM_CALENDAR_ID    NUMBER(15);
  LNUM_FIN_YR_ID      NUMBER(15);
  LNUM_QRTR_NO        NUMBER(1);
  LNUM_PERIOD_NO      NUMBER(3);
  LNUM_MIN_PERIOD_NO  NUMBER(3);
  LNUM_ADJ_PRD_ID     NUMBER(15);
  LNUM_LAST_FIN_YR_ID NUMBER(15);
  LDTE_FROM_DATE      DATE;
  LDTE_TO_DATE        DATE;
  LNUM_PERIOD_TYPE_ID NUMBER(15);
  LNUM_FIN_YR_TYPE_ID NUMBER(15);
BEGIN
  -- selection of calendar_id
  SELECT CALENDAR_ID, PERD_TYPE_ID
    INTO LNUM_CALENDAR_ID, LNUM_PERIOD_TYPE_ID
    FROM WIZ_GL_SET_OF_BOOKS
   WHERE SET_OF_BOOK_ID = PARM_SET_OF_BOOK_ID;

  -- selection from period_details
  SELECT FY.FIN_YEAR_ID, FROM_DATE, TO_DATE, QUATER_NO, PERIOD_NO,FY.FIN_YR_TYPE_ID
    INTO LNUM_FIN_YR_ID,
         LDTE_FROM_DATE,
         LDTE_TO_DATE,
         LNUM_QRTR_NO,
         LNUM_PERIOD_NO,
         LNUM_FIN_YR_TYPE_ID
    FROM PERIOD_DETAILS,FINANCIAL_YEAR_MT FY
   WHERE CALENDAR_ID = LNUM_CALENDAR_ID
     AND PERIOD_DETAILS.FIN_YEAR_ID = FY.FIN_YEAR_ID
     AND PARM_GENERATION_DATE BETWEEN FROM_DATE AND TO_DATE
     AND PERIOD_NO <> 999
